import React, { Component } from 'react'



class Controller extends Component{
    constructor(props){
        super(props)

        this.state = {
            start: true,
            pause: false,
            lap: false,
            reset: false
        }


    }


startHandler(){
    this.setState({
        ...this.state,
        start: false,
        pause: true,
        lap: true
    })

}


getController(){
    let output = null

    if(this.state.start && !this.state.reset){
        output = (
            <div>
                <button className="btn btn-sucess btn-lg px-5">
                    onClick = { event => this.startHandler()}
                    Start</button>
            </div>
        )
    } 
    return output
}


    render(){
        return this.getController()
    }

} 
export default Controller
